import {createRoot} from 'react-dom/client';



class Timer extends React.Component {
    constructor () {
        super ();
        this.state = {
            time : new Date().toLocaleTimeString()
        }
    }
render () {
    setInterval(
        () => {
          this.setState(

             { time : new Date().toLocaleTimeString() }
            
          )
        },1000
)
return (
<h2 className='timer'>
it is {this.state.time}
</h2>
)
}
}

export default Timer;
