import React from 'react';
import {createRoot} from 'react-dom/client';

var interval ;

class Timer extends React.Component {
    constructor () {
        super ();
        this.state = {
            time : 10
        }
    }

startInterval = () => {
  interval = setInterval(
        () => {
          this.setState(

             { time : this.state.time - 1 }
            
          )
        },1000
)
}  

stopInterval = () => {
clearInterval (interval) ;
}

componentDidMount () {
this.startInterval();
}

componentDidUpdate () {
if ( this.state.time == 0 )
 {
 this.stopInterval();
}
    
}

componentWillUnmount () {

}

render () {
console.log("render");

return (
  <>
  <h2 className='timer'>
  {this.state.time}
</h2>
<div className='button_box'>
<spann className='action_button start_button' onClick={this.startInterval}>Start</spann>
<spann className='action_button stop_button' onClick={this.stopInterval}>Stop</spann>
<spann className='action_button reset_button'>Reset</spann>
</div>
</>

)
}
}

export default Timer;
