    const [timerArr , settimerArr] = useState(["00:00:00" , "00:01:01"]);


    useEffect(
    () => {
    console.log("useEffect");
    return () => {

    }
    } , [isLight]
    )

   const handlesetIsLight = () => {

        setIsLight(!isLight)
    }

    return (
        <div className='main' style={{ background: isLight ? "white" : "black" }}>
            <Salam a={title} />
            <Timer
             isLight={isLight} 
             handlesetIsLight={handlesetIsLight}
             timerArr={timerArr}
             settimerArr={settimerArr}
             />
        </div>
    )

}

export default App;
