import React from 'react';
import {createRoot} from 'react-dom/client';

var interval ;

class Timer extends React.Component {
    constructor () {
        super ();
        this.state = {
            hour: 0,
            minute: 0,
            seccond: 0,
            isStart:false
        }
    }

startInterval = () => {
  if (this.state.isStart == false) {
  this.setState(
             { 
              isStart : true
              }
          )

      interval = setInterval(
        () => {
          this.setState(
             { 
              seccond:this.state.seccond + 1
              }
          )

          if (this.state.seccond == 59) {
           this.setState(
             { 
              seccond:0 ,
              minute:this.state.minute + 1
              }
          )
          }

          
          if (this.state.minute == 59) {
           this.setState(
             { 
              minute:0 ,
              hour:this.state.hour + 1
              }
          )
          }
        },1000
)
}
}  

stopInterval = () => {
clearInterval (interval) ;
}

resetInterval = () => {

}

render () {
console.log("render");

return (
  <>
  <h2 className='timer'>
  {this.state.hour + ":" + this.state.minute + ":" + this.state.seccond}
</h2>
<div className='button_box'>
<spann className='action_button start_button' onClick={this.startInterval}>Start</spann>
<spann className='action_button stop_button' onClick={this.stopInterval}>Stop</spann>
<spann className='action_button reset_button'>Reset</spann>
</div>
</>

)
}
}

export default Timer;
