handleSetTitle () {
    this.setState(
    { title : "سلام بابل"}
    ) 
}

render () {

return (
<div className='main'>
<Salam title = {this.state.title}/>
<Timer x = {this.handleSetTitle}/>
</div>
)
}
}

export default App;
