import React from 'react';
import {createRoot} from 'react-dom/client';

class App extends React.Component {

render () {

return (
<div>
<h1>salam</h1>
<h2>
it is {new Date().toLocaleTimeString()}
</h2>
</div>
)
}
}

let elem = new App ()

const tick = () => {

createRoot(document.getElementById('root')).render(elem.render());

}

setInterval(
() => {
tick();
},1000
